package com.elbrus.geektechanimation;

import androidx.appcompat.app.AppCompatActivity;
import androidx.core.view.ViewCompat;
import androidx.recyclerview.widget.RecyclerView;

import android.app.ActivityOptions;
import android.content.Intent;
import android.os.Bundle;
import android.util.Pair;
import android.view.View;
import android.widget.ImageView;
import android.widget.TextView;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity implements RecyclerAdapter.Listener {

    private RecyclerAdapter adapter;
    private ArrayList<User> users = new ArrayList<>();
    private RecyclerView recyclerView;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        recyclerView = findViewById(R.id.recycler_view);
        fillUsers();
         adapter = new RecyclerAdapter(users, this::onItemClick);
         MyAnimation.startRecyclerAnimation(recyclerView, R.anim.main_recyler_anim_layout);
         recyclerView.setAdapter(adapter);


    }


    private void fillUsers(){
        users.add(new User("Аъзамжон", "Мирзахалилов", "data:image/jpeg;base64,", 19));
        users.add(new User("Азиз", "Кашкулак", "data:image/jpeg;base64,", 17));
        users.add(new User("Азамат", "Тыныбеков", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQh9B4hNkS38VfKOTcdO-x5z_Q2huS34bfHxg&usqp=CAU", 29));
        users.add(new User("Азамат", "Куратор", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRJlgFIAW5_WsnFNYIH3IsGVgE-M_3P-3YTGw&usqp=CAU", 21));
        users.add(new User("Нурсултан", "Эркинбаев", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUlThVulL7J87nHvUAnaXUJLCHRMLigFF_aw&usqp=CAU", 26));
        users.add(new User("Миранда", "Эралиева", "data:image/jpeg;base64,", 23));
        users.add(new User("Арслан", "Жолдашев", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT9LTTG0O4iiyeE9kmXYTQnhpB-dSZglDn8jg&usqp=CAU", 22));
        users.add(new User("Тилек", "Исламов", "data:image/png;base64,", 17));
        users.add(new User("Эльбрус", "Мамедов", "data:image/jpeg;base64,", 30));
    }

    @Override
    public void onItemClick(User user, ImageView imageView, TextView name, TextView age) {
        Intent intent = new Intent(this, UserDetailActivity.class);
        intent.putExtra("user", user);

        ArrayList<Pair<View, String>> pairs = new ArrayList<>();

        pairs.add(new Pair<>(imageView, ViewCompat.getTransitionName(imageView)));
        pairs.add(new Pair<>(name, ViewCompat.getTransitionName(name)));
        pairs.add(new Pair<>(age, ViewCompat.getTransitionName(age)));

        ActivityOptions options = ActivityOptions.makeSceneTransitionAnimation(this,
                pairs.get(0),
                pairs.get(1),
                pairs.get(2));

        startActivity(intent, options.toBundle());
    }
}